            );
            this.seqClient
            .authenticate()
            .then(()=>{
                console.log("Connection to Client DB has been established successfully.")
            })
        .catch((err) =>{
            console.error('Unable to connect to the Client database:',err)
        })        
        } catch(err){
            throw err
        }
