     /**Associations */

  // this.db.models.Orders.belongsTo(this.db.models.Users,{foreignKey:'user_id'})

 this.db.models.Users.hasMany(this.db.models.Orders,{ foreignKey:'user_id',as:'orderInfo'})

      this.db.sqlClient.sync({alter:true});
      }

      async getDB(){
        return this.db
      }
}

module.exports = DB

