sourcesContent:
const bcrypt = require('bcrypt')
const {saltRounds} = require('../../config/keys')

module.exports = (sequelize,DataTypes) =>{
 const users = sequelize.define('users',{
    id:{
      type:DataTypes.INTEGER,
      autoIncrement:true,
        allowNull:false,
        primaryKey:true
    },
    uuid:{
      type:DataTypes.STRING(50),
      allowNull:false

    },
    role_id: {
      type: DataTypes.INTEGER(1),
      defaultValue: 2,
      comment:"1=admin, 2= User"
    },
    first_name:{
          type:DataTypes.STRING(200),
          allowNull:false
        },
    last_name:{
      type:DataTypes.STRING(200),
      allowNull:true
    },
    gender:{
      type:DataTypes.ENUM("male","female"),
      allowNull:true
    },
     email: {
      type: DataTypes.STRING(50),
      allowNull: false,
    },
    phone_number: {
      type: DataTypes.STRING(15),
      allowNull: true,
    },
    password:{
      type:DataTypes.STRING(100),
      allowNull:true
    },
    is_deleted:{
      type:DataTypes.BOOLEAN,
      allowNull:true,
      defaultValue:0
    }

 },{timeStamps:true,paranoid: true,underscored:true,
    hooks:{
      beforeCreate: async(user)=>{
        /**  password encryption **/

        if (user && user.password) {
          user.password = await bcrypt.hash(user.password, saltRounds);
        }
      },beforeBulkUpdate:async(user)=>{

        if (user && user.attributes && user.attributes.password) {
          // eslint-disable-next-line no-param-reassign
          user.attributes.password = await bcrypt.hash(user.attributes.password, saltRounds);
        }
      }
    }
})
 return users;
}

